/* Atlantis Admin Template JS */
$(document).ready(function() {
    // Initialize sidebar
    $('.sidebar .sidebar-wrapper, .main-panel').perfectScrollbar();

    // Initialize tooltips
    $('[data-toggle="tooltip"]').tooltip();

    // Initialize popovers
    $('[data-toggle="popover"]').popover();

    // Initialize responsive sidebar
    $('.sidebar').toggleClass('toggled');
    
    // Toggle sidebar
    $('.navbar-toggler').on('click', function() {
        $('.sidebar').toggleClass('toggled');
    });

    // Add active class to nav items based on current page
    var path = window.location.pathname;
    var activePage = $('.activePage').val();

    $('.nav-item a').each(function() {
        var href = $(this).attr('href');
        if (href === path || (activePage && $(this).attr('data-page') === activePage)) {
            $(this).addClass('active');
            $(this).closest('.nav-item').addClass('active');
            $(this).closest('.collapse').addClass('show');
            $(this).closest('.collapse').prev().removeClass('collapsed');
        }
    });
}); 